INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (72,13,6,'감자를 넣고 5분 끓여주세요.','cook_step_찜닭_06');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (73,13,7,'당근, 양파를 넣고 5분 끓여주세요.','cook_step_찜닭_07');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (74,13,8,'불린 납작당면은 10분, 일반 당면은 5분 더 끓여주세요.','cook_step_찜닭_08');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (75,13,9,'취향에 따라 대파, 떡, 버섯을 추가해주세요.','cook_step_찜닭_09');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (76,14,1,'양파 반개와 대파 반개를 썰어주세요.','cook_step_오야꼬동_01');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (77,14,2,'닭가슴살 1개를 한입 크기로 썰고 소금, 후추를 뿌려 재워주세요.','cook_step_오야꼬동_02');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (78,14,3,'간장 3스푼, 소주 1스푼, 설탕 2스푼에 물 2/3컵을 넣어 소스를 만들어주세요.','cook_step_오야꼬동_03');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (79,14,4,'계란 3개를 미리 풀어주세요.','cook_step_오야꼬동_04');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (80,14,5,'기름을 둘러 닭고기가 노릇할 때까지 완전히 구워주세요.','cook_step_오야꼬동_05');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (81,14,6,'소스와 양파, 파를 넣고 중불로 끓여주세요.','cook_step_오야꼬동_06');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (82,14,7,'계란 물을 부어 80%정도 익으면 밥에 얹어주세요.','cook_step_오야꼬동_07');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (83,15,1,'고기를 키친타올로 눌러 핏물을 제거해주세요.','cook_step_불고기_01');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (84,15,2,'먹기 좋은 크기로 자른 다음 손으로 살살 풀어주세요.','cook_step_불고기_02');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (85,15,3,'간장 5스푼, 다진마늘 1스푼, 설탕 1스푼, 매실액기스 2스푼, 물엿 2스푼, 후추 조금넣고 잘 버무려 20분간 재워주세요.','cook_step_불고기_03');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (86,15,4,'양파 반개, 당근 1/3개, 양배추 조금을 약간 큼직하게 썰어주세요.','cook_step_불고기_04');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (87,15,5,'팬에 오일을 두르고 센 불에 살살 풀어가며 구워주세요.','cook_step_불고기_05');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (88,15,6,'야채는 고기가 80%정도 익었을 때 넣어주세요.','cook_step_불고기_06');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (89,15,7,'마지막으로 다진 쪽파를 넣고 야채가 조금 덜 익었을 때 불을 끄고 잔열로 익혀주세요.','cook_step_불고기_07');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (90,16,1,'배추와 깻잎을 씻어 물기를 털고 배추 1장에 깻잎 2장을 깔고 위에는 배추 길이에 맞게 고기를 깔아주세요. 이 과정을 반복해 원하는 만큼 여러 세트를 만들어주세요.','cook_step_밀푀유나베_01');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (91,16,2,'1세트를 먹기 좋게 3등분해서 냄비에 빙 둘러준다는 느낌으로 차곡차곡 넣어주세요.','cook_step_밀푀유나베_02');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (92,16,3,'고명으로 표고버섯과 당근을 올려주세요.','cook_step_밀푀유나베_03');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (93,16,4,'육수 600ml, 멸치 장국 2스푼, 간장 2스푼, 다진 마늘 1티스푼, 소주 3스푼을 섞어 냄비에 부어주세요.','cook_step_밀푀유나베_04');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (94,16,5,'센불에 8분, 중불에 2분 끓여주세요.
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (95,17,1,'미역에 물을 부어 20분간 불려주세요.','cook_step_미역국_01');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (96,17,2,'미역이 불으면 씻어서 가위로 잘라주세요.','cook_step_미역국_02');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (97,17,3,'달군 냄비에 참기름 2스푼을 넣고 소고기를 볶아주세요.','cook_step_미역국_03');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (98,17,4,'미역과 국간장 6스푼을 넣고 볶아주세요.','cook_step_미역국_04');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (99,17,5,'뚜껑을 닫고 간이 배이게 7~10분쯤 푹 끓여주세요.','cook_step_미역국_05');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (100,17,6,'물 1.5L를 넣고 물이 끓을 때까지 센불, 물이 끓은 후에는 중불로 낮춰서 25~30분간 푹 끓여주세요.','cook_step_미역국_06');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (101,17,7,'마무리도 멸치액젓 1스푼을 넣어주세요. 간이 필요하면 간장이 아닌 소금으로 맞춰주세요.','cook_step_미역국_07');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (102,18,1,'고추장 3스푼, 간장 2스푼, 설탕 2스푼, 소주 2스푼, 고춧가루 2스푼으로 양념장을 만들어주세요.','cook_step_콩나물불고기_01');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (103,18,2,'양파 반개, 버섯, 대파 1개를 썰어주세요.','cook_step_콩나물불고기_02');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (104,18,3,'깊은 팬에 야채, 콩나물, 양념장(조금) 순으로 올려주세요.','cook_step_콩나물불고기_03');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (105,18,4,'대패삼겹살을 올리고 나머지 양념장을 올려주세요.','cook_step_콩나물불고기_04');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (106,18,5,'뚜껑을 닫고 중불로 10분동안 끓여주세요.','cook_step_콩나물불고기_05');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (107,18,6,'10분 동안 야채에서 물이 나와 촉촉해지면 센불에 전체적으로 볶아주세요.','cook_step_콩나물불고기_06');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (108,19,1,'미트볼을 뜨거운 물에 익혀주세요.','cook_step_치즈미트볼스파게티_01');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (109,19,2,'물에 소금 1티스푼을 넣고 물이 끓으면 스파게티면을 펼쳐 넣어주세요.','cook_step_치즈미트볼스파게티_02');
