INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (113,19,6,'마지막으로 미트볼과 치즈가루를 넣어주세요.','cook_step_치즈미트볼스파게티_06');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (114,20,1,'무를 네모나게 썰고, 대파를 잘게 썰어주세요.','cook_step_소고기무국_01');
