INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (116,20,3,'물 400ml를 붓고 센 불에서 끓여주세요. 중간에 나오는 거품은 살살 걷어내주세요.','cook_step_소고기무국_03');
