INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (125,21,6,'다진마늘, 고춧가루를 넣고 끓입니다.','cook_step_백종원_무된장찌개_06');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (126,21,7,'국물이 자작해지면 대파, 고추를 넣고 푹 끓입니다.','cook_step_백종원_무된장찌개_07');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (127,21,8,'완성','cook_step_백종원_무된장찌개_08');
