INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (136,23,4,'양파를 기름을 두른 팬에서 익혀줍니다.','cook_step_스팸마요덮밥_04');
