INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (139,23,7,'밥 위에 달걀과 볶은 양파와 스팸을 얹어주세요.','cook_step_스팸마요덮밥_07');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (140,23,8,'마요네즈와 쪽파를 뿌리면 완성','cook_step_스팸마요덮밥_08');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (141,24,1,'감자껍질과 씨눈을 제거합니다.','cook_step_감자전_01');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (142,24,2,'감자는 강판에 쓱쓱 갈아주세요.\n식감대신 편한 조리를 원한다면 믹서기에 갈아주시면 됩니다.','cook_step_감자전_02');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (143,24,3,'볼위에 체를 올리고 간 감자를 부어 걸러주세요.\n걸러진 감자 간 물은 절대 버리지마세요!','cook_step_감자전_03');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (144,24,4,'감자 간 물은 15분 정도 가라앉힙니다.','cook_step_감자전_04');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (145,24,5,'15분 후 감자간 윗물은 살살 따라버리세요.\n바닥에 하얗게 보이는 것이 감자 전분입니다.','cook_step_감자전_05');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (146,24,6,'볼에 물기 제거한 감자와 감자 전분, 소금 1/4큰술을 넣어줍니다.','cook_step_감자전_06');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (147,24,7,'골고루 섞어줍니다.','cook_step_감자전_07');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (148,24,8,'예열된 팬에 식용유 2큰술을 넣어줍니다.','cook_step_감자전_08');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (149,24,9,'한수저씩 놓고 모양잡아 부칩니다.','cook_step_감자전_09');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (150,24,10,'아랫면이 투명해지면 뒤집어 부칩니다.','cook_step_감자전_10');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (151,24,11,'뒤집은 아랫면도 잘 익었으면 완성','cook_step_감자전_11');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (152,25,1,'소세지에 칼집을 넣어줍니다.\n 떡은 그릇에 잠기도록 물을 부어준 다음 전자렌지에 3분 돌려줍니다. ','cook_step_소떡소떡_01');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (153,25,2,'떡과 소시지를 번갈아가며 꼬치에 끼워주세요.\n꼬치가 없다면 나무젓가락을 쓰거나 생략해도 무방합니다.','cook_step_소떡소떡_02');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (154,25,3,'후라이팬에 기름을 두르고 중불에서 꼬치를 구워주세요.','cook_step_소떡소떡_03');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (155,25,4,'이제 양념장을 만들 차례입니다. 고추장1T, 설탕2T, 케찹3T, 다진마늘1T, 물소량을 후라이팬에 넣어주세요.','cook_step_소떡소떡_04');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (156,25,5,'양념을 잘 풀어주세요. 약불에서 졸이는데 원하는 농도보다 조금 더 묽을 때 불을 꺼줍니다.','cook_step_소떡소떡_05');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (157,25,6,'완성된 양념을 구운 꼬치에 발라주세요.','cook_step_소떡소떡_06');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (158,25,7,'케첩, 머스타드, 땅콩가루를 뿌려주면 더 맛있습니다.','cook_step_소떡소떡_07');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (159,26,1,'기름을 두른 팬에 얇게 썬 마늘과 손질한 양파를 볶아 향을 내줍니다.','cook_step_에그인헬_01');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (160,26,2,'소시지와 버섯, 피망을 모두 넣고 소금, 후추로 간을 맞춰준 뒤 볶아주세요.','cook_step_에그인헬_02');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (161,26,3,'토마토소스를 넣어줍니다.','cook_step_에그인헬_03');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (162,26,4,'우유로 농도를 묽게 만들어줍니다.(우유 대신 물로도 가능합니다.)','cook_step_에그인헬_04');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (163,26,5,'모짜렐라 치즈를 둠뿍 넣어줍니다.','cook_step_에그인헬_05');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (164,26,6,'계란을 깨뜨려 올려준 뒤 뚜껑을 덮어줍니다.\n(완숙 : 2~3분, 반숙: 1분)','cook_step_에그인헬_06');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (165,26,7,'완성','cook_step_에그인헬_07');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (166,27,1,'양배추와 양파를 잘게 썰어주세요.','cook_step_오코노미야끼_01');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (167,27,2,'계란 2개를 잘 풀어준 뒤 튀김가루 1큰술과 밀가루 1큰술을 넣고 섞어주세요.','cook_step_오코노미야끼_02');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (168,27,3,'기름뺀 참치캔도 1개 넣고 섞어주세요.','cook_step_오코노미야끼_03');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (169,27,4,'썰어둔 양배추와 양파를 넣고 섞어주세요.','cook_step_오코노미야끼_04');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (170,27,5,'팬에 기름을 두르고 반죽을 펴주세요.\n그 뒤 약불로 노릇하게 구운 다음 뒤집어주세요.','cook_step_오코노미야끼_05');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (171,27,6,'위에 돈까스 소스를 넓게 펴바르고 마요네즈로 체크무늬를 그려주세요.\n 가쓰오부시를 넉넉하게 뿌려주면 완성','cook_step_오코노미야끼_06');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (172,28,1,'양파 1/2개를 채썰어주세요. 베이컨도 먹기 좋게 썰어줍니다.','cook_step_우유버터파스타_01');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (173,28,2,'팬에 물 500ml, 우유 300ml, 버터 2조각(40g)을 넣은 뒤 파스타면을 바로 넣고 끓여주세요.\n (저지방, 무지방 우유 말고 그냥 우유를 사용하세요.)','cook_step_우유버터파스타_02');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (174,28,3,'강불에 10분간 끓이고 불을 낮춰주세요.','cook_step_우유버터파스타_03');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (175,28,4,'우유가 좀 꾸덕해지면 양파, 베이컨, 굴소스 1큰술을 넣고 중불로 10분간 끓여주세요.','cook_step_우유버터파스타_04');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (176,28,5,'더 꾸덕해지면 다진마늘 1큰술도 넣어주세요.','cook_step_우유버터파스타_05');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (177,28,6,'마무리로 후추, 파슬리, 파마산치즈가루를 뿌리면 완성(선택)','cook_step_우유버터파스타_06');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (178,29,1,'김치와 삼겹살을 미리 한입크기로 잘게 잘라서 준비해주세요.','cook_step_삼겹살김치볶음_01');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (179,29,2,'달군 팬에 올리브유 1큰술을 넣고 김치를 넣어 1분 정도 볶아주세요,','cook_step_삼겹살김치볶음_02');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (180,29,3,'설탕 1큰술을 넣은 뒤 살짝 볶아주고 삼겹살을 넣어주세요.','cook_step_삼겹살김치볶음_03');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (181,29,4,'삼겹살이 좀 익을 정도로 볶아주다가 간장을 2큰술 넣어주세요. 짜게 드시기 싫으시다면 1큰술만 넣어주세요.','cook_step_삼겹살김치볶음_04');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (182,29,5,'불을 낮춰 간장을 졸여주며 볶아줍니다. 마지막으로 고춧가루 2ts을 넣어주세요.\n기호에 따라 고춧가루를 더 넣으셔도 됩니다.','cook_step_삼겹살김치볶음_05');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (183,29,6,'완성!','cook_step_삼겹살김치볶음_06');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (184,30,1,'대파, 파프리카, 양파를 썰어주세요.(대파: 송송 썰기 / 파프리카,양파: 깍둑썰기)\n소세지도 적당한 크기로 썰어주세요.\n파프리카 외 다른 채소로 대체할 수 있어요!','cook_step_케찹_오므라이스_01');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (185,30,2,'팬에 식용유를 두르고 대파를 볶아 파기름을 내주세요. 파향이 고소하게 올라오면 양파를 넣고 볶습니다.','cook_step_케찹_오므라이스_02');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (186,30,3,'양파가 반투명해지면 소세지를 넣고 볶습니다.','cook_step_케찹_오므라이스_03');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (187,30,4,'마지막으로 파프리카를 넣은 후 소금과 후추로 간을 맞춰주세요.','cook_step_케찹_오므라이스_04');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (188,30,5,'한 김 식은밥을 넣어서 잘 풀어준 뒤 케찹을 넣어 볶아주세요.','cook_step_케찹_오므라이스_05');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (189,30,6,'인당 계란 3개를 풀어주세요. 팬에 기름을 두르고 계란물을 부어준 후 스크램블 애그를 만드는 것처럼 휘저어주세요.','cook_step_케찹_오므라이스_06');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (190,30,7,'접시에 볶음밥을 담고 그 위에 계란이불을 덮어줍니다.','cook_step_케찹_오므라이스_07');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (191,30,8,'마지막으로 케찹을 뿌려주면 완성','cook_step_케찹_오므라이스_08');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (192,31,1,'양파를 동그랗게 자르고 분리하여 얇은 껍질을 최대한 제거해 주세요.','cook_step_어니언링_01.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (193,31,2,'튀김가루 5스푼, 소금 1티스푼, 물 3스푼, 계란 1개를 넣고 잘 섞어주세요.','cook_step_어니언링_02.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (194,31,3,'비닐봉지에 튀김가루와 양파를 넣고 잘 흔들어 준 뒤, 앞서 만든 반죽을 묻히고 빵가루를 충분히 입혀줍니다.','cook_step_어니언링_03.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (195,31,4,'깊은 팬에 기름을 넉넉히 두르고 튀겨주면 완성.','cook_step_어니언링_04.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (196,32,1,'식빵은 모두 테두리를 제거해 주세요','cook_step_에그샌드위치_01.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (197,32,2,'계란을 모두 삶은 뒤, 으깨어 주세요. 그 후, 으깬 계란에 마요네즈, 머스타드, 후추를 잘 섞어 줍니다.','cook_step_에그샌드위치_02.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (198,32,3,'식빵에 으깬 계란을 잘 발라 샌드위치로 만들면 완성','cook_step_에그샌드위치_03.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (199,33,1,'대파를 줄기 방향으로 반으로 가른뒤 큼직하게 썰어주세요. 고추, 마늘, 삼겹살은 적당히 잘라주세요.','cook_step_제육볶음_01.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (200,33,1,'손질한 삼겹살을 프라이 팬에 잘 깔아 준 뒤, 약불로 양면을 구워주세요. 기름이 충분히 나오지 않는다면 식용유를 살짝 넣어주세요.','cook_step_제육볶음_02.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (201,33,2,'삼겹살을 구웠다면 마늘을 넣어주세요. 이때 기름이 너무 많다면 조금 빼주세요.','cook_step_제육볶음_03.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (202,33,3,'마늘이 어느정도 익었다면 준비한 설탕을 넣어주세요. 설탕이 다 녹았다면 간장을 넣어주세요.','cook_step_제육볶음_04.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (203,33,4,'간장이 끓는다면 손질한 대파와 고춧가루를 전부 넣어주세요. 이때 물의 양은 불의 세기에 따라 조절해서 넣어주세요.','cook_step_제육볶음_05.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (204,33,5,'마지막으로 참기름을 살짝 둘러 마무리하면 완성','cook_step_제육볶음_06.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (205,34,1,'채소는 먹기 좋은 크기로 썰어 주세요.','cook_step_소세지야채볶음_01.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (206,34,2,'소세지는 끓는 물에 데쳐 주세요.','cook_step_소세지야채볶음_02.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (207,34,3,'팬에 채소를 볶아주세요. 소금, 후추로 간을 합니다.','cook_step_소세지야채볶음_03.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (208,34,4,'중간에 데친 소세지를 넣고 볶아주고 치즈를 살짝 얹으면 완성. 치즈는 없어도 됩니다.','cook_step_소세지야채볶음_04.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (209,35,1,'연어는 키친타올로 물기를 제거한 뒤, 껍질을 벗기고 한입 크기로 썰어 주세요.','cook_step_연어장_01.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (210,35,2,'간장 1컵, 설탕 0.5컵을 냄비에 넣고 설탕이 녹을 때 까지 가열하고 식혀줍니다.','cook_step_연어장_02.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (211,35,3,'양파도 한입 크기로 썰어 준 뒤, 유리통에 연어와 양파를 깔아줍니다. 충분히 식힌 간장을 부어주면 완성','cook_step_연어장_03.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (212,36,1,'오이 3개를 한입 크기로 썰어줍니다.','cook_step_오이무침_01.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (213,36,2,'간장 2스푼, 설탕 1티스푼, 식초 1티스푼, 참기름 1티스푼을 오이에 넣고 잘 버무려 줍니다.','cook_step_오이무침_02.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (214,36,3,'만약 다진 마늘이 있다면 0.5스푼을 넣고 버무리면 완성','cook_step_오이무침_03.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (215,37,1,'고추장, 설탕, 간장, 간마늘, 고춧가루, 맛술을 1스푼씩 같은 비율로 모두 넣고 섞어 줍니다. 만든 양념장은 냉장고에서 3시간 이상 숙성시키면 더 맛있어요.','cook_step_닭갈비_01.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (216,37,2,'닭 정육과 준비한 채소도 전부 먹기 좋은 크기로 잘라주세요.','cook_step_닭갈비_02.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (217,37,3,'자른 닭고기와 채소, 양념을 버무려 주세요. 후추와 참기름이 있다면 같이 넣어주세요.','cook_step_닭갈비_03.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (218,37,4,'중불에서 양념이 타지 않게 물을 살짝 넣어가며 익히면 완성.','cook_step_닭갈비_04.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (219,38,1,'마늘을 다져 준 뒤, 고추장, 간장, 설탕을 잘 섞어주세요.','cook_step_고추장삼겹살_01.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (220,38,2,'팬에서 삼겹살을 먼저 익히고 양념을 발라 더 익혀주면 완성','cook_step_고추장삼겹살_01.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (221,39,1,'계란을 전부 삶아서 껍질을 깝니다.','cook_step_계란장조림_01.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (222,39,2,'물 1리터, 간장 1.5컵, 설탕 0.7컵, 물엿 0.25컵을 넣고 가열하여 녹여주세요. 물엿이 없다면 설탕은 1컵을 사용합니다.','cook_step_계란장조림_02.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (223,39,3,'녹인 간장 소스에 계란을 넣고 색이 마음에 들 때까지 약 30분 간 끓여줍니다.','cook_step_계란장조림_03.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (224,39,4,'꽈리고추는 이쑤시개로 구멍을 송송 뚫어주세요. 마늘은 편으로 썰어주세요.','cook_step_계란장조림_04.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (225,39,5,'준비한 고추와 마늘은 넣고 약 1분간 더 끓여주세요. 충분히 식힌 뒤, 통에 넣으면 완성','cook_step_계란장조림_05.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (226,40,1,'중불에서 만두 바닥을 익히고 약불로 줄여줍니다. 물 0.5컵을 넣고 뚜껑을 닫아 줍니다.','cook_step_군만두_01.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (227,40,2,'물이 졸 때까지 약불로 굽습니다.','cook_step_군만두_02.png');
INSERT INTO recipe_process (recipe_process_id, recipe_id, recipe_process_sequence, recipe_process_description, recipe_process_image) VALUES (228,40,3,'간장, 식초, 물을 같은 비율로 섞어 주면 완성. 고춧가루도 기호에 따라 넣어주세요.','cook_step_군만두_03.png');
